            $("ul.scroll").css({marginLeft: x + "px"});
        })
    });
    function width_li () {
        a = 0;
        $("ul.scroll li").each(function() {
            a += parseInt($(this).width()) + parseInt($(this).css("paddingLeft")) + parseInt($(this).css("paddingRight"))+2;
